const result = JSON.parse($input.first().json.content.parts[0].text);

const prompt = result.final_prompt;

// Prepare the parts array with the text prompt first
const parts = [
  { text: prompt }
];

// Loop through all incoming items and extract images
// Note: If all images are in one item as different binary keys, adjust the loop.
// This assumes multiple items (e.g., 3 separate image items).
let x = 1;
for (const item of $('Prepare Image').first().json.parsed) {
  if (!result.excluded_images.includes(x)) {
parts.push({
      inlineData: {
        mimeType: item.mime,
        data: item.data // n8n already provides this as base64
      }
    });
  }
 x++;
}

// Return the structure ready for the API
return {
  json: {
    payload: {
      contents: [
        {
          parts: parts
        }
      ],
      generationConfig: {
          responseModalities: ["image"],
          imageConfig:{
               aspectRatio: "16:9"
          }
      }
    }
  }
};
