function getValidJsonObject(rawText) {

    if (typeof rawText !== 'string' || rawText.trim() === '') {
        console.error("Invalid input or empty text field.");
        return null;
    }

    let jsonString = rawText.trim();

    // 2. Check for and remove the markdown JSON block fences.
    // This regex is robust and handles variations in whitespace.
    const markdownRegex = /^```json\s*([\s\S]*?)\s*```$/;
    const match = jsonString.match(markdownRegex);

    if (match) {
        // If it's a match, the actual JSON string is in the first capture group.
        jsonString = match[1];
    }

    // 3. Parse the cleaned string into a JSON object.
    try {
        return JSON.parse(jsonString);
    } catch (error) {
        console.error("Failed to parse JSON string:", error);
        console.error("Problematic string:", jsonString);
        return null;
    }
}



